svg.script:
var paper_best = {

  dom_change_handler: function(evt) {
    console.log('dom_change_handler', this.id, evt.detail)

    howmany_tspan = this.querySelector(
      `#${this.id} > :not(.contents_group) #tspan_rule_num`
    )

    let howMany = parseInt(howmany_tspan.textContent)
    let sign = howMany / Math.abs(howMany) // -1 or +1
    let tally = []

    paper.visit_contents_group(this, (dieElem) => {
      tally.sort((a,b) => (a-b))
      let tempNum = paper.get_die_value(dieElem)
      if (tempNum !== undefined) {
        tempNum = sign * tempNum
        if (tally.length < Math.abs(howMany)) {
          tally.push( tempNum )
        } else if (tempNum > tally[0]) {
          tally[0] = tempNum
        }
      }
    })
    let tspan = this.querySelector(`#${this.id} > text > #tspan_result`)
    tspan.textContent = sign * tally.reduce((x,y) => x+y, 0)
  },

  initialize: function(elem, prototype) {
    elem.addEventListener('dom_change', this.dom_change_handler)
    elem.addEventListener('resize', paper.resize_handler.bind(elem))
  },

  menu: {
    'Roll All': {
      eventName: 'paper_roll',
      applicable: (node) => { return true },
      handler: function(evt) {
        return paper.roll_handler(this, evt)
      },
    },
    'Best +': {
      eventName: 'rule_best_plus',
      applicable: (node) => { return true },
      handler: function() {
        paper_best.best_change_handler(this, +1)
      },
    },
    'Best -': {
      eventName: 'rule_best_minus',
      applicable: (node) => { return true },
      handler: function() {
        paper_best.best_change_handler(this, -1)
      },
    },
  },

  best_change_handler: function(elem, change) {
    console.log('contents_change_handler', elem.id, change)
    howmany_tspan = elem.querySelector(
      `#${elem.id} > :not(.contents_group) #tspan_rule_num`
    )
    howMany = parseInt(howmany_tspan.textContent)
    let sum = howMany + change
    howmany_tspan.textContent = sum
    elem.dispatchEvent(new CustomEvent('dom_change', {
      bubbles: true,
      detail: { 'ruleElemId': elem.id },
    }))
  },


}
