svg.script:
var paper_count = {

  drop_handler: function(evt) {
    return paper.drop_handler(this, evt)
  },

  dom_change_handler: function(evt) {
    console.log('dom_change_handler', this.id, evt.detail)
    condition_tspan = this.querySelector(
      `#${this.id} > :not(.contents_group) #tspan_rule_num`
    )
    let gate = parseInt(condition_tspan.textContent)

    let count = 0

    paper.visit_contents_group(this, (s) => {
      let tempNum = 0
      s.querySelectorAll('tspan').forEach((t) => {
        if (t.closest('svg').id !== s.id) {
          // it's buried multiple levels deep in sub-SVGs
          // so skip it lest it be double-counted
          return
        }
        c = t.textContent.trim()
        num = parseInt(c)
        if (isNaN(num)) {
          if (c == '+') {
            tempNum = 1
          } else if (c == '-') {
            tempNum = -1
          }
        } else {
          tempNum =  num
        }
      })
      count = count + (tempNum >= gate ? 1 : 0)
    })
    tspan = this.querySelector(
      `#${this.id} > :not(.contents_group) #tspan_result`
    )
    tspan.textContent = count
  },

  drag_handler: function(evt) {
    if (!this.contains(evt.target)) {
      return
    }
    console.log('inner element being dragged', evt.target.id)
  },

  label_click_handler: function() {
    paper.label_click_handler(this)
  },
  label_change_handler: function(evt) {
    paper.label_change_handler(this, evt)
  },

  initialize: function(elem, prototype) {
    elem.addEventListener('dom_change', this.dom_change_handler)
    elem.addEventListener('label_change', this.label_change_handler)

    elem.addEventListener('svg_drag', this.drag_handler)
    elem.addEventListener('svg_drop', this.drop_handler)
    elem.addEventListener('svg_dragenter', () => {console.log('E')})
    elem.addEventListener('svg_dragleave', () => {console.log('L')})

    elem.addEventListener('resize', this.resize_handler)

    paper.initialize(elem, prototype)
  },

  resize_handler: function(evt) {
    return paper.resize_handler(this, evt)
  },

  menu: {
    'Roll All': {
      eventName: 'paper_roll',
      applicable: (node) => { return true },
      handler: function(evt) {
        return paper.roll_handler(this, evt)
      },
    },
    'Label': {
      eventName: 'label_click',
      applicable: (node) => { return true },
      handler: function() {
        paper.label_click_handler(this)
      },
    },
    'Fix': {
      eventName: 'paper_fix',
      applicable: (node) => { return true },
      handler: function(evt) {
        paper.fix(evt, this)
      },
    },
    'Cond +': {
      eventName: 'rule_count_cond_plus',
      applicable: (node) => { return true },
      handler: function() {
        paper_count.cond_change_handler(this, +1)
      },
    },
    'Cond -': {
      eventName: 'rule_count_cond_minus',
      applicable: (node) => { return true },
      handler: function() {
        paper_count.cond_change_handler(this, -1)
      },
    },
  },

  cond_change_handler: function(elem, change) {
    console.log('contents_change_handler', elem.id, change)
    condition_tspan = elem.querySelector( `#${elem.id} > :not(.contents_group) #tspan_rule_num`)
    gate = parseInt(condition_tspan.textContent)
    condition_tspan.textContent = gate + change
    elem.dispatchEvent(new CustomEvent('dom_change', {
      bubbles: true,
      detail: { 'ruleElemId': elem.id },
    }))
  },

}
