svg.script:
var paper_modifier = {

  dom_change_handler: function(evt) {
    console.log('dom_change_handler', this.id, evt.detail)
    modifier_tspan = this.querySelector(
      `#${this.id} > :not(.contents_group) #tspan_rule_num`
    )
    let modifier = parseInt(modifier_tspan.textContent)

    let sum = 0

    paper.visit_contents_group(this, (s) => {
      let tempNum = 0
      s.querySelectorAll('tspan').forEach((t) => {
        if (t.closest('svg').id !== s.id) {
          // it's buried multiple levels deep in sub-SVGs
          // so skip it lest it be double-counted
          return
        }
        c = t.textContent.trim()
        num = parseInt(c)
        if (isNaN(num)) {
          if (c == '+') {
            tempNum = 1
          } else if (c == '-') {
            tempNum = -1
          }
        } else {
          tempNum =  num
        }
      })
      sum = sum + tempNum
    })
    tspan = this.querySelector(
      `#${this.id} > :not(.contents_group) #tspan_result`
    )
    tspan.textContent = sum + modifier
  },

  initialize: function(elem, prototype) {
    elem.addEventListener('dom_change', this.dom_change_handler)
    elem.addEventListener('resize', paper.resize_handler.bind(elem))
  },

  menu: {
    'Roll All': {
      eventName: 'paper_roll',
      applicable: (node) => { return true },
      handler: function(evt) {
        return paper.roll_handler(this, evt)
      },
    },
    'Mod +': {
      eventName: 'rule_modifier_plus',
      applicable: (node) => { return true },
      handler: function() {
        paper_modifier.mod_change_handler(this, +1)
      },
    },
    'Mod -': {
      eventName: 'rule_modifier_minus',
      applicable: (node) => { return true },
      handler: function() {
        paper_modifier.mod_change_handler(this, -1)
      },
    },
  },

  mod_change_handler: function(elem, change) {
    console.log('contents_change_handler', elem.id, change)
    modifier_tspan = elem.querySelector(
      `#${elem.id} > :not(.contents_group) #tspan_rule_num`
    )
    modifier = parseInt(modifier_tspan.textContent)
    let sum = modifier + change
    modifier_tspan.textContent = sum < 1 ? sum : '+' + sum
    elem.dispatchEvent(new CustomEvent('dom_change', {
      bubbles: true,
      detail: { 'ruleElemId': elem.id },
    }))
  },

}
